mapper.insert:

            select last_insert_id() as id

		insert into question(user_id, user_name, topic_id, topic_name, title, content, gmt_create, gmt_modified)
		values(#{userId}, #{userName}, #{topicId}, #{topicName}, #{title}, #{content}, NOW(), NOW());

mapper.select:
select id, user_id, user_name, topic_id, topic_name, title, content, scan_count, answer_count, gmt_create, gmt_modified
from question
where gmt_modified > #{time}
limit #{count}

select id, user_id, user_name, topic_id, topic_name, title, content, scan_count, answer_count, gmt_create, gmt_modified
from question
where gmt_modified <= #{time}
order by gmt_modified desc
limit #{count}

select id, user_id, user_name, topic_id, topic_name, title, content, scan_count, answer_count, gmt_create, gmt_modified
from question
where gmt_modified > #{time} and
topic_id in (
	select topic_id from user_concern_topic where user_id=#{id}
)
limit #{count}

select id, user_id, user_name, topic_id, topic_name, title, content, scan_count, answer_count, gmt_create, gmt_modified
from question
where gmt_modified <= #{time} and
topic_id in (
	select topic_id from user_concern_topic where user_id=#{id}
)
order by gmt_modified desc
limit #{count}
